import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setProducts } from '../features/productsSlice';
import { addToCart } from '../features/cartSlice';
import ProductCard from './ProductCard';

const ProductList = () => {
  const dispatch = useDispatch();
  const products = useSelector((state) => state.products.filteredItems);

  useEffect(() => {
    const mockProducts = [
      { 
        id: 1, 
        name: 'Intel CPU Core i3', 
        price: 5900, 
        category: 'A', 
        image: 'https://example.com/images/intel-i3.jpg' // ใส่ URL ของรูปสินค้า
      },
      { 
        id: 2, 
        name: 'Intel CPU Core i7', 
        price: 7900, 
        category: 'A', 
        image: 'https://example.com/images/intel-i7.jpg' // ใส่ URL ของรูปสินค้า
      },
      { 
        id: 3, 
        name: 'Intel CPU Core i9', 
        price: 9900, 
        category: 'A', 
        image: 'https://example.com/images/intel-i9.jpg' // ใส่ URL ของรูปสินค้า
      },
      { 
        id: 4, 
        name: 'Ram Corsair 16 GB Bus 5200Mhz', 
        price: 2500, 
        category: 'B', 
        image: 'https://example.com/images/ram-corsair-16gb.jpg' // ใส่ URL ของรูปสินค้า
      },
      { 
        id: 5, 
        name: 'Ram Corsair 32 GB Bus 5200Mhz', 
        price: 4500, 
        category: 'B', 
        image: 'https://example.com/images/ram-corsair-32gb.jpg' // ใส่ URL ของรูปสินค้า
      },
      { 
        id: 6, 
        name: 'Ram Corsair 64 GB Bus 5200Mhz', 
        price: 5500, 
        category: 'B', 
        image: 'https://example.com/images/ram-corsair-64gb.jpg' // ใส่ URL ของรูปสินค้า
      },
      { 
        id: 7, 
        name: 'Nvidia RTX 4070 TI', 
        price: 11900, 
        category: 'C', 
        image: 'https://example.com/images/nvidia-rtx-4070ti.jpg' // ใส่ URL ของรูปสินค้า
      },
      { 
        id: 8, 
        name: 'Nvidia RTX 4080 TI', 
        price: 15900, 
        category: 'C', 
        image: 'https://example.com/images/nvidia-rtx-4080ti.jpg' // ใส่ URL ของรูปสินค้า
      },
      { 
        id: 9, 
        name: 'Nvidia RTX 4090 TI', 
        price: 18900, 
        category: 'C', 
        image: 'https://example.com/images/nvidia-rtx-4090ti.jpg' // ใส่ URL ของรูปสินค้า
      },
    ];
    dispatch(setProducts(mockProducts));
  }, [dispatch]);

  const handleAddToCart = (product) => {
    dispatch(addToCart(product));
  };

  return (
    <div className="container py-5">
      <h2 className="text-center mb-5" style={{ fontSize: '2.5rem', fontWeight: '700', color: '#333' }}>รายการสินค้า</h2>
      <div className="row">
        {products.map((product) => (
          <div key={product.id} className="col-lg-4 col-md-6 col-sm-12 mb-4">
            <div className="card custom-card border-0 shadow-custom rounded-3 overflow-hidden">
              <img src={product.image} alt={product.name} className="card-img-top product-img" />
              <div className="card-body p-4">
                <h5 className="card-title product-title">{product.name}</h5>
                <p className="card-text product-price">฿{product.price}</p>
                <button
                  className="btn btn-custom w-100"
                  onClick={() => handleAddToCart(product)}
                >
                  <i className="fas fa-cart-plus"></i> เพิ่มลงในตะกร้า
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
