// src/components/ProductFilter.js
import React from 'react';
import { useDispatch } from 'react-redux';
import { filterByCategory, sortByPrice } from '../features/productsSlice';

const ProductFilter = () => {
  const dispatch = useDispatch();

  const handleCategoryChange = (event) => {
    dispatch(filterByCategory(event.target.value));
  };

  const handleSortChange = (event) => {
    dispatch(sortByPrice(event.target.value));
  };

  return (
    <div className="container py-4">
      <div className="row justify-content-between align-items-center">
        {/* Category Filter */}
        <div className="col-md-5 mb-3 mb-md-0">
          <label htmlFor="category" className="form-label text-muted">Filter by Category</label>
          <select
            id="category"
            className="form-select form-select-lg shadow-sm"
            onChange={handleCategoryChange}
          >
            <option value="">All Categories</option>
            <option value="A">CPU</option>
            <option value="B">Ram</option>
            <option value="C">Graphic Card</option>
          </select>
        </div>

        {/* Price Sort Filter */}
        <div className="col-md-/">
          <label htmlFor="sortByPrice" className="form-label text-muted">Sort by Price</label>
          <select
            id="sortByPrice"
            className="form-select form-select-lg shadow-sm"
            onChange={handleSortChange}
          >
            <option value="low-to-high">Price: Low to High</option>
            <option value="high-to-low">Price: High to Low</option>
          </select>
        </div>
      </div>
    </div>
  );
};

export default ProductFilter;
