// src/pages/CartPage.js
import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart } from '../features/cartSlice';
import Navbar from '../components/Navbar';  // Import Navbar
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css'; // สำหรับไอคอน

const CartPage = () => {
  const dispatch = useDispatch();
  const cartItems = useSelector((state) => state.cart.cartItems);

  const handleRemove = (item) => {
    dispatch(removeFromCart(item));
  };

  const totalAmount = cartItems.reduce((total, item) => total + item.price, 0);

  const handleCheckout = () => {
    alert("ทำการสั่งซื้อเรียบร้อยแล้ว!");
    // ที่นี่คุณสามารถเพิ่มฟังก์ชันเพิ่มเติม เช่น การเรียก API เพื่อบันทึกข้อมูลการสั่งซื้อ
  };

  return (
    <div>
      <Navbar />  {/* เพิ่ม Navbar ที่นี่ */}
      <h2 className="text-center mb-4">ตะกร้าสินค้า</h2>
      {cartItems.length === 0 ? (
        <p className="text-center">ตะกร้าสินค้าว่างเปล่า</p>
      ) : (
        <div>
          <ul className="list-group mb-4">
            {cartItems.map((item) => (
              <li key={item.id} className="list-group-item d-flex justify-content-between align-items-center">
                <div>
                  {item.name} - ${item.price}
                </div>
                <button className="btn btn-danger" onClick={() => handleRemove(item)}>Remove</button>
              </li>
            ))}
          </ul>
          <h3>Total: ${totalAmount}</h3>
          <button className="btn btn-success w-5 mt-5" onClick={handleCheckout}>
            สั่งซื้อ
          </button>
        </div>
      )}
    </div>
  );
};

export default CartPage;
