import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setProducts } from '../features/productsSlice';
import { addToCart } from '../features/cartSlice';
import ProductCard from './ProductCard';

const ProductList = () => {
  const dispatch = useDispatch();
  const products = useSelector((state) => state.products.filteredItems);

  useEffect(() => {
    const mockProducts = [
      { id: 1, name: 'Intel CPU Core i3', price: 5900, category: 'A', image: '/images/i3.webp' },
      { id: 2, name: 'Intel CPU Core i5', price: 7900, category: 'A', image: '/images/i5.webp' },
      { id: 3, name: 'Intel CPU Core i9', price: 9900, category: 'A', image: '/images/i9.webp' },
      { id: 4, name: 'Ram Corsair 16 GB Bus 5200Mhz', price: 2500, category: 'B', image: '/images/ram.webp' },
      { id: 5, name: 'Ram Corsair 32 GB Bus 5200Mhz', price: 4500, category: 'B', image: '/images/ram.webp' },
      { id: 6, name: 'Ram Corsair 64 GB Bus 5200Mhz', price: 5500, category: 'B', image: '/images/ram.webp' },
      { id: 7, name: 'Nvidia RTX 4060', price: 11900, category: 'C', image: '/images/4060.webp' },
      { id: 8, name: 'Nvidia RTX 4070 TI', price: 15900, category: 'C', image: '/images/4070.webp' },
      { id: 9, name: 'Nvidia RTX 4080 TI', price: 18900, category: 'C', image: '/images/4080.webp' },
    ];
    dispatch(setProducts(mockProducts));
  }, [dispatch]);

  const handleAddToCart = (product) => {
    dispatch(addToCart(product));
  };

  return (
    <div className="container py-5">
      <h2 className="text-center mb-5" style={{ fontSize: '2.5rem', fontWeight: '700', color: '#333' }}>
        รายการสินค้า
      </h2>
      <div className="row">
        {products.map((product) => (
          <div key={product.id} className="col-lg-3 col-md-4 col-sm-6 mb-4"> {/* ปรับเป็น 4 คอลัมน์ */}
            <div className="card custom-card border-0 shadow-lg rounded-3 overflow-hidden">
              <img
                src={product.image}
                alt={product.name}
                className="card-img-top product-img"
                style={{ objectFit: 'cover', height: '250px' }} // ปรับขนาดรูปให้พอดีกับการ์ด
              />
              <div className="card-body p-4">
                <h5 className="card-title product-title" style={{ fontSize: '1.25rem', fontWeight: '600' }}>
                  {product.name}
                </h5>
                <p className="card-text product-price" style={{ fontSize: '1.2rem', color: '#FF5733', fontWeight: 'bold' }}>
                  ฿{product.price}
                </p>
                <button
                  className="btn btn-primary w-100 mt-3"
                  onClick={() => handleAddToCart(product)}
                  style={{ backgroundColor: '#007bff', border: 'none', fontWeight: '600' }}
                >
                  <i className="fas fa-cart-plus"></i> เพิ่มลงในตะกร้า
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
