import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart } from '../features/cartSlice';
import Navbar from '../components/Navbar'; // Import Navbar
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css'; // สำหรับไอคอน

const CartPage = () => {
  const dispatch = useDispatch();
  const cartItems = useSelector((state) => state.cart.cartItems);  

  const handleRemove = (item) => {
    dispatch(removeFromCart(item));
  };

  const totalAmount = cartItems.reduce((total, item) => total + item.price, 0);

  return (
    <div>
      <Navbar />  {/* เพิ่ม Navbar ที่นี่ */}
      <div className="container py-5">
        <h2 className="text-center mb-4">ตะกร้าสินค้า</h2>
        {cartItems.length === 0 ? (
          <p className="text-center">ตะกร้าสินค้าว่างเปล่า</p>
        ) : (
          <div>
            <div className="row">
              {cartItems.map((item) => (
                <div key={item.id} className="col-md-4 mb-4">
                  <div className="card shadow-sm">
                    <img 
                      src={item.image || "https://via.placeholder.com/150"} 
                      alt={item.name} 
                      className="card-img-top" 
                      style={{ objectFit: 'cover', height: '225px' }} 
                    />
                    <div className="card-body">
                      <h5 className="card-title">{item.name}</h5>
                      <p className="card-text">${item.price}</p>
                      <button 
                        className="btn btn-danger" 
                        onClick={() => handleRemove(item)}
                      >
                        Remove
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
            <h3 className="text-right">Total: ${totalAmount}</h3>
          </div>
        )}
      </div>
    </div>
  );
};

export default CartPage;
