import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setProducts } from '../features/productsSlice';
import { addToCart } from '../features/cartSlice';
import ProductCard from './ProductCard';

const ProductList = () => {
  const dispatch = useDispatch();
  const products = useSelector((state) => state.products.filteredItems);

  useEffect(() => {
    const mockProducts = [
      { id: 1, name: 'Intel CPU Core i3', price: 5900, category: 'A', image: 'https://via.placeholder.com/150' },
      { id: 2, name: 'Intel CPU Core i7', price: 7900, category: 'A', image: 'https://via.placeholder.com/150' },
      { id: 3, name: 'Intel CPU Core i9', price: 9900, category: 'A', image: 'https://via.placeholder.com/150' },
      { id: 4, name: 'Ram Corsair 16 GB Bus 5200Mhz', price: 2500, category: 'B', image: 'https://via.placeholder.com/150' },
      { id: 5, name: 'Ram Corsair 32 GB Bus 5200Mhz', price: 4500, category: 'B', image: 'https://via.placeholder.com/150' },
      { id: 6, name: 'Ram Corsair 64 GB Bus 5200Mhz', price: 5500, category: 'B', image: 'https://via.placeholder.com/150' },
      { id: 7, name: 'Nvidia RTX 4070 TI', price: 11900, category: 'C', image: 'https://via.placeholder.com/150' },
      { id: 8, name: 'Nvidia RTX 4080 TI', price: 15900, category: 'C', image: 'https://via.placeholder.com/150' },
      { id: 9, name: 'Nvidia RTX 4090 TI', price: 18900, category: 'C', image: 'https://via.placeholder.com/150' },
    ];
    dispatch(setProducts(mockProducts));
  }, [dispatch]);

  const handleAddToCart = (product) => {
    dispatch(addToCart(product));
  };

  return (
    <div className="container py-5">
      <h2 className="text-center mb-5" style={{ fontSize: '2rem', fontWeight: 'bold', color: '#333' }}>รายการสินค้า</h2>
      <div className="row">
        {products.map((product) => (
          <div key={product.id} className="col-lg-4 col-md-6 col-sm-12 mb-4"> {/* Responsive grid system */}
            <div className="card shadow-sm rounded-lg border-0 overflow-hidden">
              <img src={product.image} alt={product.name} className="card-img-top" style={{ height: '200px', objectFit: 'cover' }} />
              <div className="card-body">
                <h5 className="card-title" style={{ fontSize: '1.1rem', fontWeight: '500' }}>{product.name}</h5>
                <p className="card-text" style={{ fontSize: '0.9rem', color: '#555' }}>฿{product.price}</p>
                <button
                  className="btn btn-primary w-100"
                  onClick={() => handleAddToCart(product)}
                  style={{ fontWeight: 'bold', backgroundColor: '#007bff', borderColor: '#007bff' }}
                >
                  Add to Cart
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
