import React from 'react';
import Navbar from '../components/Navbar';  // Import Navbar
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart } from '../features/cartSlice';
import '../styles/Cartpage.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css'; // สำหรับไอคอน

const CartPage = () => {
  const dispatch = useDispatch();
  const cartItems = useSelector((state) => state.cart.cartItems); // ดึงข้อมูลจาก Redux

  const handleRemove = (item) => {
    dispatch(removeFromCart(item)); // ลบสินค้าจากตะกร้า
  };

  const totalAmount = cartItems.reduce((total, item) => total + item.price, 0); // คำนวณราคา

  return (
    <div>
      <Navbar />  {/* ใส่ Navbar ที่นี่ */}
      <h2 className="text-center my-4">Shopping Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p> // ถ้าไม่มีสินค้าในตะกร้า
      ) : (
        <div>
          <ul>
            {cartItems.map((item) => (
              <li key={item.id}>
                {item.name} - ${item.price}
                <button onClick={() => handleRemove(item)}>Remove</button> {/* ลบสินค้า */}
              </li>
            ))}
          </ul>
          <h3>Total: ${totalAmount}</h3>
        </div>
      )}
    </div>
  );
};

export default CartPage;
