// src/components/ProductFilter.js
import React from 'react';
import { useDispatch } from 'react-redux';
import { filterByCategory, sortByPrice } from '../features/productsSlice'; // นำเข้า action

const ProductFilter = () => {
  const dispatch = useDispatch();

  const handleCategoryChange = (event) => {
    dispatch(filterByCategory(event.target.value)); // ส่งค่า category ไปยัง Redux
  };

  const handleSortChange = (event) => {
    dispatch(sortByPrice(event.target.value)); // ส่งค่า sort ไปยัง Redux
  };

  return (
    <div>
      <label htmlFor="category">Category</label>
      <select id="category" onChange={handleCategoryChange}>
        <option value="">All Categories</option>
        <option value="A">Category A</option>
        <option value="B">Category B</option>
        <option value="C">Category C</option>
      </select>

      <label htmlFor="price">Sort by Price</label>
      <select id="price" onChange={handleSortChange}>
        <option value="low-to-high">Price: Low to High</option>
        <option value="high-to-low">Price: High to Low</option>
      </select>
    </div>
  );
};

export default ProductFilter;
