import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setProducts } from '../features/productsSlice';
import { addToCart } from '../features/cartSlice';
import 'bootstrap/dist/css/bootstrap.min.css'; // นำเข้า Bootstrap

const ProductList = () => {
  const dispatch = useDispatch();
  const products = useSelector((state) => state.products.filteredItems);

  useEffect(() => {
    // Mock API call to fetch products
    const mockProducts = [
      { id: 1, name: 'Product 1', price: 100, category: 'A', image: 'https://via.placeholder.com/150' },
      { id: 2, name: 'Product 2', price: 200, category: 'B', image: 'https://via.placeholder.com/150' },
      { id: 3, name: 'Product 3', price: 300, category: 'C', image: 'https://via.placeholder.com/150' },
      // Add more mock data as needed
    ];
    dispatch(setProducts(mockProducts));
  }, [dispatch]);

  const handleAddToCart = (product) => {
    dispatch(addToCart(product));
  };

  return (
    <div className="row">
      {products.map((product) => (
        <div className="col-md-4 mb-4" key={product.id}>
          <div className="card">
            <img src={product.image} className="card-img-top" alt={product.name} />
            <div className="card-body">
              <h5 className="card-title">{product.name}</h5>
              <p className="card-text">${product.price}</p>
              <button onClick={() => handleAddToCart(product)} className="btn btn-primary">Add to Cart</button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ProductList;
