import React from 'react';

const ProductCard = ({ product }) => {
  return (
    <div className="max-w-xs w-full p-4">
      <div className="bg-white shadow-lg rounded-lg overflow-hidden hover:shadow-xl transition-all">
        <img src={product.image} alt={product.name} className="w-full h-48 object-cover" />
        <div className="p-6">
          <h3 className="text-xl font-semibold text-gray-900">{product.name}</h3>
          <p className="text-lg text-blue-600">${product.price}</p>
          <button className="bg-blue-600 text-white py-2 px-6 mt-4 rounded-md w-full hover:bg-blue-700 transition-all">
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
