import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart } from '../features/cartSlice';
import '../styles/Cartpage.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css'; // สำหรับไอคอน

const CartPage = () => {
  const dispatch = useDispatch();
  const cartItems = useSelector((state) => state.cart.cartItems); // ดึงข้อมูลจาก Redux

  const handleRemove = (item) => {
    dispatch(removeFromCart(item)); // ลบสินค้าจากตะกร้า
  };

  const totalAmount = cartItems.reduce((total, item) => total + item.price, 0); // คำนวณราคา

  return (
    <div className="container my-5">
      <h2 className="mb-4">Shopping Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p> // ถ้าไม่มีสินค้าในตะกร้า
      ) : (
        <div>
          <ul className="list-group mb-3">
            {cartItems.map((item) => (
              <li key={item.id} className="list-group-item d-flex justify-content-between align-items-center">
                <span>{item.name} - ${item.price}</span>
                <button 
                  onClick={() => handleRemove(item)} 
                  className="btn btn-danger btn-sm">
                  <i className="bi bi-trash"></i> Remove
                </button>
              </li>
            ))}
          </ul>
          <h3>Total: ${totalAmount.toFixed(2)}</h3> {/* แสดงยอดรวม */}
          <button className="btn btn-primary mt-3">Proceed to Checkout</button> {/* ปุ่มชำระเงิน */}
        </div>
      )}
    </div>
  );
};

export default CartPage;
