// src/components/ProductCard.js
import React from 'react';

const ProductCard = ({ product, handleAddToCart }) => {
  return (
    <div className="card shadow-sm border-light rounded">
      <img
        src={product.image}
        alt={product.name}
        className="card-img-top"
        style={{ height: '200px', objectFit: 'cover' }} // เพิ่ม inline styling ให้รูปใหญ่ขึ้น
      />
      <div className="card-body">
        <h5 className="card-title">{product.name}</h5>
        <p className="card-text text-primary">${product.price}</p>
        <button
          className="btn btn-primary w-100" // เปลี่ยน w-10 เป็น w-100 เพื่อให้ปุ่มเต็มความกว้าง
          onClick={() => handleAddToCart(product)}
        >
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
