// src/features/cartSlice.js
import { createSlice } from '@reduxjs/toolkit';

// ฟังก์ชันนี้จะไม่ได้อยู่ใน reducers โดยตรง
const loadCartFromLocalStorage = () => {
  try {
    const savedCart = localStorage.getItem('cart');
    if (savedCart) {
      return JSON.parse(savedCart);
    }
  } catch (error) {
    console.error('Error loading cart from localStorage', error);
  }
  return [];
};

const initialState = {
  cartItems: loadCartFromLocalStorage(), // ใช้ฟังก์ชันนี้เพื่อดึงข้อมูลจาก localStorage
};

export const cartSlice = createSlice({
  name: 'cart',
  initialState,
  reducers: {
    addToCart: (state, action) => {
      state.cartItems.push(action.payload);
      localStorage.setItem('cart', JSON.stringify(state.cartItems)); // บันทึกข้อมูลลง localStorage
    },
    removeFromCart: (state, action) => {
      state.cartItems = state.cartItems.filter(item => item.id !== action.payload.id);
      localStorage.setItem('cart', JSON.stringify(state.cartItems)); // บันทึกข้อมูลลง localStorage
    },
  },
});

// export เฉพาะ actions ที่เกี่ยวข้อง
export const { addToCart, removeFromCart } = cartSlice.actions;

export default cartSlice.reducer;
