import React from 'react';

const CartPage = ({ cartItems, total }) => {
  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-4xl font-bold text-gray-900 mb-6">Your Cart</h1>
      <div className="bg-white shadow-lg rounded-lg p-6 mb-6">
        {cartItems.length === 0 ? (
          <p className="text-gray-600 text-lg">Your cart is empty.</p>
        ) : (
          <ul>
            {cartItems.map(item => (
              <li key={item.id} className="flex justify-between py-2 border-b border-gray-300">
                <span className="text-gray-900">{item.name}</span>
                <span className="text-gray-700">${item.price}</span>
              </li>
            ))}
          </ul>
        )}
        <div className="flex justify-between items-center mt-6">
          <p className="text-2xl font-semibold text-gray-900">Total: ${total}</p>
          <button className="bg-blue-600 text-white py-2 px-6 rounded-md hover:bg-blue-700 transition-all">
            Checkout
          </button>
        </div>
      </div>
    </div>
  );
};

export default CartPage;
